<?php/** @copyright   Copyright (c) 2016 www.magebuzz.com*/class Magebuzz_Privatesale_Block_Adminhtml_Invite_Invitelist extends Mage_Adminhtml_Block_Widget_Grid {  public function __construct() {    parent::__construct();    $this->setId('inviteGrid');    $this->setDefaultSort('invite_id');    $this->setDefaultDir('ASC');    $this->setSaveParametersInSession(TRUE);  }  protected function _prepareCollection() {    $collection = Mage::getModel('privatesale/invite')->getCollection();    $this->setCollection($collection);    return parent::_prepareCollection();  }  protected function _prepareColumns() {    $this->addColumn('invite_id', array('header' => Mage::helper('privatesale')->__('ID'), 'index' => 'invite_id', 'width' => '80px'));    $this->addColumn('referer_email', array('header' => Mage::helper('privatesale')->__('Invited By'), 'index' => 'referer_email',));    $this->addColumn('invited_customer_email', array('header' => Mage::helper('privatesale')->__('Sent To'), 'index' => 'invited_customer_email',));    $this->addColumn('invited_customer_sign_up_code', array('header' => Mage::helper('privatesale')->__('Invite Code'), 'index' => 'invited_customer_sign_up_code',));    return parent::_prepareColumns();  }  public function getRowUrl($row) {    return; //$this->getUrl('*/*/view', array('id' => $row->getId()));  }}