<?php/** @copyright Copyright (c) 2016 www.magebuzz.com*/class Magebuzz_Privatesale_Block_Adminhtml_Waiting_Waiting extends Mage_Adminhtml_Block_Widget_Grid_Container {  public function __construct() {    $this->_headerText = Mage::helper('privatesale')->__('Invite List');    parent::__construct();    $this->removeButton('add');    $this->_addButton('clear_waiting', array('label' => Mage::helper('sales')->__('Clear Waiting List'), 'onclick' => 'setLocation(\'' . $this->getUrl('adminhtml/privatesale_waiting/clear') . '\')',));  }  protected function _prepareLayout() {    $this->setChild('grid', $this->getLayout()->createBlock('privatesale/adminhtml_waiting_gridwaiting')->setSaveParametersInSession(TRUE));  }}