<?php/** @copyright Copyright (c) 2016 www.magebuzz.com*/class Magebuzz_Privatesale_Block_Landing extends Mage_Core_Block_Template {  public function getLoginFormHtml() {    return $this->getLayout()->createBlock('privatesale/landing')->setTemplate('privatesale/customer/account/login.phtml')->toHtml();  }  public function actionFormLogin() {    return Mage::getUrl('customer/account/loginPost');  }  public function actionFormRegister() {    return Mage::getUrl('customer/account/createpost');  }  public function actionFormForgot() {    return Mage::getUrl('customer/account/forgotpasswordpost');  }}